import React, { Component } from "react";
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  Dimensions,
  Image,
  TouchableOpacity,
  Linking,
  RefreshControl
} from "react-native";
import { LineChart } from "react-native-chart-kit";
import CoronaImage from "../../assets/home.jpg";
import axios from "axios";
import { AntDesign } from "@expo/vector-icons";

const red = "#FC312F";
const green = "#29AF62";

const { width, height } = Dimensions.get("window");

export default class Home extends Component {
  state = {
    loading: true,
    dataRecovered: {},
    totalCases: "",
    dataConfirmed: {},
    recoveredCases: ""
  };
  async componentDidMount() {
    let resConfirmed = await axios.get(
      "https://api.covid19api.com/country/india/status/confirmed/live"
    );
    let dataConfirmed = resConfirmed.data.map(entry => {
      const { Cases } = entry;
      return Cases;
    });
    let resRecovered = await axios.get(
      "https://api.covid19api.com/country/india/status/recovered/live"
    );
    let dataRecovered = resRecovered.data.map(entry => {
      const { Cases } = entry;
      return Cases;
    });
    let indiaData = await axios.get("https://api.covid19api.com/summary");
    let totalCases;
    let totalDeaths;
    let newCases;
    let newDeaths;
    let totalRecovered;
    let newRecovered;
    indiaData.data.Countries.forEach(country => {
      if (country.Country == "India") {
        totalCases = country.TotalConfirmed;
        totalDeaths = country.TotalDeaths;
        totalRecovered = country.TotalRecovered;
        newCases = country.NewConfirmed;
        newDeaths = country.NewDeaths;
        newRecovered = country.NewRecovered;
      }
    });
    let newsRes = await axios.get(
      "https://newsapi.org/v2/top-headlines?country=in&q=corona&apiKey=1a54f1fa7c7741d28b862ba1a32875ef"
    );
    let news = newsRes.data.articles;
    this.setState({
      dataRecovered,
      dataConfirmed,
      loading: false,
      totalCases,
      totalDeaths,
      newCases,
      newDeaths,
      totalRecovered,
      newRecovered,
      news
    });
  }
  onRefresh = async () => {
    this.setState({
      refreshing: true,
      loading: true
    });
    let resConfirmed = await axios.get(
      "https://api.covid19api.com/country/india/status/confirmed/live"
    );
    let dataConfirmed = resConfirmed.data.map(entry => {
      const { Cases } = entry;
      return Cases;
    });
    let resRecovered = await axios.get(
      "https://api.covid19api.com/country/india/status/recovered/live"
    );
    let dataRecovered = resRecovered.data.map(entry => {
      const { Cases } = entry;
      return Cases;
    });
    let indiaData = await axios.get("https://api.covid19api.com/summary");
    let totalCases;
    let totalDeaths;
    let newCases;
    let newDeaths;
    let totalRecovered;
    let newRecovered;
    indiaData.data.Countries.forEach(country => {
      if (country.Country == "India") {
        totalCases = country.TotalConfirmed;
        totalDeaths = country.TotalDeaths;
        totalRecovered = country.TotalRecovered;
        newCases = country.NewConfirmed;
        newDeaths = country.NewDeaths;
        newRecovered = country.NewRecovered;
      }
    });
    let newsRes = await axios.get(
      "http://newsapi.org/v2/top-headlines?country=in&q=corona&apiKey=1a54f1fa7c7741d28b862ba1a32875ef"
    );
    let news = newsRes.data.articles;
    this.setState({
      dataRecovered,
      dataConfirmed,
      loading: false,
      totalCases,
      totalDeaths,
      newCases,
      newDeaths,
      totalRecovered,
      newRecovered,
      news,
      refreshing: false
    });
  };
  render() {
    const {
      loading,
      dataConfirmed,
      dataRecovered,
      totalCases,
      totalDeaths,
      newCases,
      newDeaths,
      totalRecovered,
      newRecovered
    } = this.state;
    if (loading) {
      return (
        <View
          style={{ flex: 1, justifyContent: "center", alignItems: "center" }}
        >
          <Image
            style={{ resizeMode: "contain", height: 300, width: 300 }}
            source={require("../../assets/loading.gif")}
          />
        </View>
      );
    }
    return (
      <View style={styles.container}>
        <ScrollView
          refreshControl={
            <RefreshControl
              refreshing={this.state.refreshing}
              onRefresh={this.onRefresh}
            />
          }
        >
          <Image source={CoronaImage} style={styles.headerImage} />

          <View style={styles.content}>
            <View style={{ height: height * 0.3 }}>
              <ScrollView
                horizontal={true}
                showsHorizontalScrollIndicator={false}
              >
                <View
                  style={{
                    height: height * 0.25,
                    width: width * 0.75,
                    flexDirection: "row",
                    position: "relative",
                    backgroundColor: "#fff",
                    borderRadius: 16,
                    padding: 10
                  }}
                >
                  <View
                    style={{
                      flex: 1,
                      justifyContent: "center",
                      alignItems: "flex-start"
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 16,
                        color: "gray"
                      }}
                    >
                      Total Cases {"  "}
                      <AntDesign color="#FC312F" name="caretup" />
                    </Text>
                    <Text
                      style={{
                        fontSize: 27,
                        fontWeight: "bold",
                        opacity: 0.8
                      }}
                    >
                      {totalCases}
                    </Text>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "center",
                      position: "absolute",
                      flex: 1,
                      marginLeft: width * 0.1
                    }}
                  >
                    <LineChart
                      data={{
                        datasets: [
                          {
                            data: [
                              dataConfirmed[0],
                              dataConfirmed[(dataConfirmed.length - 1) / 2],
                              dataConfirmed[
                                ((dataConfirmed.length - 1) * 3) / 4
                              ],
                              dataConfirmed[dataConfirmed.length - 1]
                            ]
                          }
                        ]
                      }}
                      width={width * 0.7} // from react-native
                      height={height * 0.2}
                      yAxisInterval={1} // optional, defaults to 1
                      formatYLabel={label => ""}
                      chartConfig={{
                        color: (opacity = 1) => `#FC312F`,
                        backgroundGradientFrom: "#fff",
                        backgroundGradientFromOpacity: 0,
                        backgroundGradientTo: "#fff",
                        backgroundGradientToOpacity: 0,
                        strokeWidth: 2, // optional, default 3
                        barPercentage: 0.5,
                        propsForBackgroundLines: {
                          opacity: 0
                        }
                      }}
                      bezier
                      style={{
                        marginVertical: 8,
                        borderRadius: 16
                      }}
                    />
                  </View>
                </View>
                {/* recovered starts here -------------- */}
                <View
                  style={{
                    height: height * 0.25,
                    width: width * 0.75,
                    flexDirection: "row",
                    position: "relative",
                    backgroundColor: "#fff",
                    borderRadius: 16,
                    padding: 10,
                    marginLeft: width * 0.1
                  }}
                >
                  <View
                    style={{
                      flex: 1,
                      justifyContent: "center",
                      alignItems: "flex-start"
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 16,
                        color: "gray"
                      }}
                    >
                      Recovered {"  "}
                      <AntDesign color="#29AF62" name="caretdown" />
                    </Text>
                    <Text
                      style={{
                        fontSize: 27,
                        fontWeight: "bold",
                        opacity: 0.8
                      }}
                    >
                      {totalRecovered}
                    </Text>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "center",
                      position: "absolute",
                      flex: 1,
                      marginLeft: width * 0.1
                    }}
                  >
                    <LineChart
                      data={{
                        datasets: [
                          {
                            data: [
                              dataRecovered[0],
                              dataRecovered[(dataRecovered.length - 1) / 2],
                              dataRecovered[
                                ((dataRecovered.length - 1) * 3) / 4
                              ],
                              dataRecovered[dataRecovered.length - 1]
                            ]
                          }
                        ]
                      }}
                      width={width * 0.7} // from react-native
                      height={height * 0.2}
                      yAxisInterval={1} // optional, defaults to 1
                      formatYLabel={label => ""}
                      chartConfig={{
                        color: (opacity = 1) => `#29AF62`,
                        backgroundGradientFrom: "#fff",
                        backgroundGradientFromOpacity: 0,
                        backgroundGradientTo: "#fff",
                        backgroundGradientToOpacity: 0,
                        strokeWidth: 2, // optional, default 3
                        barPercentage: 0.5,
                        propsForBackgroundLines: {
                          opacity: 0
                        }
                      }}
                      bezier
                      style={{
                        marginVertical: 8,
                        borderRadius: 16
                      }}
                    />
                  </View>
                </View>
                {/* total deaths shuru */}
                <View
                  style={{
                    height: height * 0.25,
                    width: width * 0.75,
                    flexDirection: "row",
                    position: "relative",
                    backgroundColor: "#fff",
                    borderRadius: 16,
                    padding: 10,
                    marginLeft: width * 0.1
                  }}
                >
                  <View
                    style={{
                      flex: 1,
                      justifyContent: "center",
                      alignItems: "flex-start"
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 16,
                        color: "gray"
                      }}
                    >
                      Total Deaths {"  "}
                      <AntDesign color="#FC312F" name="caretup" />
                    </Text>
                    <Text
                      style={{
                        fontSize: 27,
                        fontWeight: "bold",
                        opacity: 0.8
                      }}
                    >
                      {totalDeaths}
                    </Text>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "center",
                      position: "absolute",
                      flex: 1,
                      marginLeft: width * 0.2
                    }}
                  >
                    <LineChart
                      data={{
                        datasets: [
                          {
                            data: [0, parseInt(totalDeaths)]
                          }
                        ]
                      }}
                      width={width * 0.7} // from react-native
                      height={height * 0.2}
                      yAxisInterval={1} // optional, defaults to 1
                      formatYLabel={label => ""}
                      chartConfig={{
                        color: (opacity = 1) => `#FC312F`,
                        backgroundGradientFrom: "#fff",
                        backgroundGradientFromOpacity: 0,
                        backgroundGradientTo: "#fff",
                        backgroundGradientToOpacity: 0,
                        strokeWidth: 2, // optional, default 3
                        barPercentage: 0.5,
                        propsForBackgroundLines: {
                          opacity: 0
                        }
                      }}
                      bezier
                      style={{
                        marginVertical: 8,
                        borderRadius: 16
                      }}
                    />
                  </View>
                </View>
              </ScrollView>
            </View>
            <View style={styles.moreInfoConatiner}>
              <Text
                style={{
                  opacity: 0.7,
                  fontSize: 24,
                  fontWeight: "bold"
                }}
              >
                COVID-19 India Stats
              </Text>
              <Text
                style={{
                  opacity: 0.7,
                  fontSize: 20,
                  fontWeight: "bold",
                  color: "gray",
                  marginTop: 20
                }}
              >
                New Cases{"          "}
                <Text style={{ color: red }}>{newCases}</Text>
              </Text>
              <Text
                style={{
                  opacity: 0.7,
                  fontSize: 20,
                  fontWeight: "bold",
                  color: "gray",
                  marginTop: 10
                }}
              >
                New Recovered{"  "}
                <Text style={{ color: green }}>{newRecovered}</Text>
              </Text>
              <Text
                style={{
                  opacity: 0.7,
                  fontSize: 20,
                  fontWeight: "bold",
                  color: "gray",
                  marginTop: 10
                }}
              >
                New Deaths{"         "}
                <Text style={{ color: red }}>{newDeaths}</Text>
              </Text>
              <Text
                style={{
                  textAlign: "right",
                  color: "gray",
                  fontSize: 16,
                  marginTop: 20
                }}
              >
                Made by Sunny Dhama
              </Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white",
                paddingBottom: 20,
                marginTop: 20,
                borderRadius: 16
              }}
            >
              <Image
                style={{
                  width: width - 50,
                  resizeMode: "contain",
                  height: 200,
                  borderRadius: 16,
                  marginTop: 10
                }}
                source={require("../../assets/map.png")}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "#F2F2F2",
                paddingBottom: 20,
                marginTop: 20,
                borderRadius: 16
              }}
            >
              <Image
                style={{
                  width: width - 50,
                  resizeMode: "contain",
                  height: 300,
                  borderRadius: 16,
                  marginTop: 10
                }}
                source={require("../../assets/pmdonate.png")}
              />
            </View>
            <Text
              style={{
                textAlign: "left",
                backgroundColor: "white",
                opacity: 1,
                fontSize: 27,
                color: "#1f1f1f",
                paddingLeft: 20,
                marginTop: 20,
                borderTopLeftRadius: 16,
                borderTopRightRadius: 16,
                paddingTop: 20
              }}
            >
              What you should do?
            </Text>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white"
              }}
            >
              <Image
                style={styles.imagesPrecaution}
                source={require("../../assets/01.png")}
              />
              <Image
                style={styles.imagesPrecaution}
                source={require("../../assets/02.png")}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white"
              }}
            >
              <Text style={{ flex: 1, fontSize: 16, textAlign: "center" }}>
                Wash Hands
              </Text>
              <Text style={{ flex: 1, fontSize: 16, textAlign: "center" }}>
                Wear Mask
              </Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white"
              }}
            >
              <Image
                style={styles.imagesPrecaution}
                source={require("../../assets/03.png")}
              />
              <Image
                style={styles.imagesPrecaution}
                source={require("../../assets/04.png")}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white"
              }}
            >
              <Text style={{ flex: 1, fontSize: 16, textAlign: "center" }}>
                Use Sanitizers
              </Text>
              <Text style={{ flex: 1, fontSize: 16, textAlign: "center" }}>
                Respect Lockdown
              </Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white"
              }}
            >
              <Image
                style={styles.imagesPrecaution}
                source={require("../../assets/05.png")}
              />
              <Image
                style={styles.imagesPrecaution}
                source={require("../../assets/06.png")}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white",
                paddingBottom: 20
              }}
            >
              <Text style={{ flex: 1, fontSize: 16, textAlign: "center" }}>
                Prefer Namaste
              </Text>
              <Text style={{ flex: 1, fontSize: 16, textAlign: "center" }}>
                Stay Safe
              </Text>
            </View>
            <Text
              style={{
                textAlign: "left",
                backgroundColor: "white",
                opacity: 1,
                fontSize: 27,
                color: "#1f1f1f",
                paddingLeft: 20,
                marginTop: 30,
                borderTopLeftRadius: 16,
                borderTopRightRadius: 16,
                paddingTop: 20
              }}
            >
              Symptoms
            </Text>

            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white",
                paddingBottom: 20
              }}
            >
              <Image
                style={{
                  width: width - 50,
                  resizeMode: "contain",
                  height: 200,
                  borderRadius: 10,
                  marginTop: 10
                }}
                source={require("../../assets/symptoms.png")}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white",
                paddingBottom: 20
              }}
            >
              <Image
                style={[styles.imagesPrecaution, { flex: 1, borderRadius: 10 }]}
                source={require("../../assets/cough.gif")}
              />
              <Text style={{ flex: 1, textAlign: "center" }}>Dry Cough</Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white",
                paddingBottom: 20
              }}
            >
              <Image
                style={[styles.imagesPrecaution, { flex: 1, borderRadius: 10 }]}
                source={require("../../assets/fever.gif")}
              />
              <Text style={{ flex: 1, textAlign: "center" }}>High Fever</Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                backgroundColor: "white",
                paddingBottom: 20
              }}
            >
              <Image
                style={[styles.imagesPrecaution, { flex: 1, borderRadius: 10 }]}
                source={require("../../assets/breathelessness.gif")}
              />
              <Text style={{ flex: 1, textAlign: "center" }}>
                Difficulty in Breathing
              </Text>
            </View>
            <View style={{ marginTop: 30, borderRadius: 16 }}>
              <View
                style={{
                  backgroundColor: "white",
                  padding: 20,
                  borderRadius: 16
                }}
              >
                <Text
                  style={{
                    textAlign: "left",
                    backgroundColor: "white",
                    opacity: 1,
                    fontSize: 27,
                    color: "#1f1f1f"
                  }}
                >
                  Latest News
                </Text>
                {this.state.news.map((newsPost, _id) => (
                  <TouchableOpacity
                    key={_id}
                    style={{
                      borderBottomColor: "#d9d9d9",
                      borderBottomWidth: 1,
                      paddingVertical: 20,
                      borderRadius: 16
                    }}
                    onPress={() => Linking.openURL(newsPost.url)}
                  >
                    <View
                      style={{
                        flexDirection: "row",
                        alignItems: "center",
                        marginBottom: 10
                      }}
                    >
                      <Image
                        style={{
                          resizeMode: "cover",
                          height: 60,
                          width: 60,
                          borderRadius: 10
                        }}
                        source={{ uri: newsPost.urlToImage }}
                      />
                      <View style={{ paddingHorizontal: 20 }}>
                        <Text
                          style={{
                            fontSize: 16,
                            color: "#1f1f1f",
                            fontWeight: "bold"
                          }}
                        >
                          {newsPost.author}
                        </Text>
                        <Text
                          style={{
                            fontSize: 16,
                            color: "gray",
                            fontWeight: "bold"
                          }}
                        >
                          {newsPost.source.name}
                        </Text>
                      </View>
                    </View>
                    <Text
                      style={{
                        fontSize: 16,
                        fontWeight: "bold",
                        opacity: 0.8
                      }}
                    >
                      {newsPost.title}
                    </Text>
                    <Text
                      style={{
                        fontSize: 14,
                        opacity: 0.6
                      }}
                    >
                      {`${newsPost.description}...click to read more.`}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            </View>
          </View>
        </ScrollView>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F2F4F5"
  },
  headerImage: {
    width,
    resizeMode: "contain",
    height: height * 0.4
  },
  content: {
    paddingHorizontal: 20,
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    marginTop: -40,
    paddingTop: 40,
    backgroundColor: "#F2F4F5",
    flex: 1
  },
  moreInfoConatiner: {
    backgroundColor: "white",
    borderRadius: 16,
    flex: 1,
    padding: 20
  },
  imagesPrecaution: {
    resizeMode: "contain",
    width: width * 0.3,
    height: width * 0.3
  }
});
